import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './App.css';

const API_TIMEOUT = 10000;
const MAX_RETRIES = 3;

function App() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState({
    isConnected: false,
    lastChecked: null
  });

  const createAxiosInstance = useCallback(() => {
    const instance = axios.create({
      baseURL: process.env.REACT_APP_API_URL || 'http://localhost:5000/api',
      timeout: API_TIMEOUT,
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      },
      withCredentials: false  // Changed from true to false for initial debugging
    });

    // Improved error interceptor
    instance.interceptors.response.use(
      response => response,
      error => {
        console.error('Axios Interceptor Error:', error);

        if (!error.response) {
          // Network error or server is not responding
          setMessage('Network error. Please check your connection.');
          setConnectionStatus({
            isConnected: false,
            lastChecked: new Date().toLocaleString()
          });
        } else {
          // Server responded with an error
          setMessage(error.response?.data?.message || 'An unexpected error occurred');
        }

        return Promise.reject(error);
      }
    );

    return instance;
  }, []);

  const handleApiCall = useCallback(async (apiCall, errorMessage) => {
    const performApiCall = async (retriesLeft) => {
      try {
        setIsLoading(true);
        const result = await apiCall();
        setMessage(result.message || 'Operation successful');
        setIsLoading(false);
        return result;
      } catch (error) {
        console.error('API Error:', error);

        if (retriesLeft === 1) {
          const errorMsg = error.response?.data?.message || 
                           error.message || 
                           errorMessage;

          setMessage(errorMsg);
          setIsLoading(false);

          // Update connection status if it's a network error
          if (!error.response) {
            setConnectionStatus({
              isConnected: false,
              lastChecked: new Date().toLocaleString()
            });
          }

          throw error;
        }

        // Wait before retrying
        await new Promise(resolve => setTimeout(resolve, 1000 * (MAX_RETRIES - retriesLeft + 1)));
        return performApiCall(retriesLeft - 1);
      }
    };

    return performApiCall(MAX_RETRIES);
  }, []);

  const testConnection = useCallback(async () => {
    const api = createAxiosInstance();
    try {
      await api.get('/health');
      setConnectionStatus({
        isConnected: true,
        lastChecked: new Date().toLocaleString()
      });
      setMessage('Server connection established successfully');
    } catch (error) {
      console.error('Connection Test Error:', error);
      setConnectionStatus({
        isConnected: false,
        lastChecked: new Date().toLocaleString()
      });
      setMessage('Failed to connect to server');
    }
  }, [createAxiosInstance]);

  const fetchUsers = useCallback(async () => {
    const api = createAxiosInstance();
    await handleApiCall(
      async () => {
        const response = await api.get('/users');
        setUsers(response.data.data || []);
        return response.data;
      },
      'Failed to fetch users'
    );
  }, [createAxiosInstance, handleApiCall]);

  const addUser = useCallback(async (e) => {
    e.preventDefault();
    const api = createAxiosInstance();
    await handleApiCall(
      async () => {
        const response = await api.post('/users', { name, email });
        setName('');
        setEmail('');
        await fetchUsers();
        return response.data;
      },
      'Failed to add user'
    );
  }, [createAxiosInstance, handleApiCall, fetchUsers, name, email]);

  const deleteUser = useCallback(async (userId) => {
    const api = createAxiosInstance();
    await handleApiCall(
      async () => {
        const response = await api.delete(`/users/${userId}`);
        await fetchUsers();
        return response.data;
      },
      'Failed to delete user'
    );
  }, [createAxiosInstance, handleApiCall, fetchUsers]);

  useEffect(() => {
    testConnection();
    fetchUsers();
  }, [testConnection, fetchUsers]);

  return (
    <div className="container">
      <div className="form-wrapper">
        <h1>User Management App</h1>

        <div className={`connection-status ${connectionStatus.isConnected ? 'connected' : 'disconnected'}`}>
          Server Status: {connectionStatus.isConnected ? 'Connected' : 'Disconnected'}
          <small>Last checked: {connectionStatus.lastChecked || 'Never'}</small>
          <button onClick={testConnection} disabled={isLoading}>
            Recheck Connection
          </button>
        </div>

        {message && (
          <div className={`message ${message.includes('Failed') || message.includes('error') ? 'error' : 'success'}`}>
            {message}
          </div>
        )}

        <form onSubmit={addUser} className="user-form">
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            disabled={!connectionStatus.isConnected || isLoading}
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            disabled={!connectionStatus.isConnected || isLoading}
          />
          <button 
            type="submit" 
            disabled={!connectionStatus.isConnected || isLoading}
          >
            {isLoading ? 'Processing...' : 'Add User'}
          </button>
        </form>

        {users.length > 0 && (
          <div className="users-list">
            <h2>Users ({users.length})</h2>
            <ul>
              {users.map(user => (
                <li key={user._id}>
                  <div>
                    <strong>{user.name}</strong>
                    <span>{user.email}</span>
                  </div>
                  <button
                    onClick={() => deleteUser(user._id)}
                    disabled={isLoading}
                  >
                    Delete
                  </button>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
