import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './App.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function App() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState({
    isConnected: false,
    lastChecked: null
  });

  const api = axios.create({
    baseURL: API_URL,
    timeout: 10000
  });

  const testConnection = useCallback(async () => {
    try {
      await api.get('/health');
      setConnectionStatus({
        isConnected: true,
        lastChecked: new Date().toLocaleString()
      });
      setMessage('Server connected');
    } catch (error) {
      console.error('Connection Error:', error);
      setConnectionStatus({
        isConnected: false,
        lastChecked: new Date().toLocaleString()
      });
      setMessage(`Connection failed: ${error.message}`);
    }
  }, []);

  const fetchUsers = useCallback(async () => {
    try {
      const response = await api.get('/users');
      setUsers(response.data.data || []);
    } catch (error) {
      setMessage(`Fetch error: ${error.message}`);
    }
  }, []);

  const addUser = async (e) => {
    e.preventDefault();
    try {
      setIsLoading(true);
      await api.post('/users', { name, email });
      setName('');
      setEmail('');
      await fetchUsers();
      setMessage('User added');
    } catch (error) {
      setMessage(`Add user error: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  const deleteUser = async (userId) => {
    try {
      setIsLoading(true);
      await api.delete(`/users/${userId}`);
      await fetchUsers();
      setMessage('User deleted');
    } catch (error) {
      setMessage(`Delete error: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    testConnection();
    fetchUsers();
  }, [testConnection, fetchUsers]);

  return (
    <div className="container">
      <h1>User Management App</h1>

      <div className={`connection-status ${connectionStatus.isConnected ? 'connected' : 'disconnected'}`}>
        Status: {connectionStatus.isConnected ? 'Connected' : 'Disconnected'}
        <button onClick={testConnection}>Recheck</button>
      </div>

      {message && <div className="message">{message}</div>}

      <form onSubmit={addUser}>
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          disabled={isLoading}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          disabled={isLoading}
        />
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Processing...' : 'Add User'}
        </button>
      </form>

      <div className="users-list">
        <h2>Users ({users.length})</h2>
        {users.map(user => (
          <div key={user._id}>
            {user.name} - {user.email}
            <button 
              onClick={() => deleteUser(user._id)}
              disabled={isLoading}
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
